import{PlatformHelpModel}from"../../model/help/platform-help-model.min.js";import{Utility}from"../../shared/utility.min.js";import ScriptSeriesLoader from"../../shared/script-series-loader.min.js";import StylesheetSeriesLoader from"../../shared/stylesheet-series-loader.min.js";class PlatformHelpController{constructor(){this.model=new PlatformHelpModel,this.stylesheetLoader=StylesheetSeriesLoader,this.stylesheetLoader.add("css/work-experience.min.css"),this.stylesheetLoader.load(),this.scriptLoader=ScriptSeriesLoader,this.scriptLoader.add("https://ajax.googleapis.com/ajax/libs/jquery/3.3.1/jquery.min.js"),this.scriptLoader.load()}static build=async()=>{const e=new PlatformHelpController;for(;e.scriptLoader.running;)await Utility.sleep(2e3);return e};execute=()=>{const e=this.model.getPlatforms();for(let t=0;t<e.length;t++){const r=e[t],s=[];s.push(this.createNoTd(t)),s.push(this.createNameTd(r)),s.push(this.createDescriptionTd(r));const o=`<tr>${s.join("")}</tr>`;$("#help-info tr:last").after(o)}};createTd=e=>`<td class='help-info-cell'>${e}</td>`;createNoTd=e=>{const t=e+1;return this.createTd(`<p style='text-align:center;'>${t}</p>`)};createNameTd=e=>this.createTd(`<p class="work-experience-technology-platform" style="white-space:nowrap;">${e.name}</p>`);createDescriptionTd=e=>this.createTd(`<p>${e.description}</p>`)}export{PlatformHelpController};