import{WorkingHoursRepository}from"../../repository/working-hours-repository.min.js";import{MathUtility}from"../../shared/utility/math-utility.min.js";class BeingLateHoursGraphModel{constructor(){const r=new WorkingHoursRepository;this.records=r.getAll()}getActual=()=>{const r=[];for(const t of this.records)r.push({yearMonth:`${t.year}-${t.month}`,value:t.beingLate.hours});return r};getEMA=()=>{const r=MathUtility.calculateEMA(this.records.map((r=>r.beingLate.hours)),12),t=[];for(let o=0;o<this.records.length;o++)t.push({yearMonth:`${this.records[o].year}-${this.records[o].month}`,value:r[o]});return t};getAverage=()=>{const r=MathUtility.calculateAverage(this.records.map((r=>r.beingLate.hours))),t=[];for(const o of this.records)t.push({yearMonth:`${o.year}-${o.month}`,value:r});return t}}export{BeingLateHoursGraphModel};