import{WorkingHoursRepository}from"../../repository/working-hours-repository.js";import{Utility}from"../../shared/utility.js";class BeingLateNumberGraphModel{constructor(){const r=new WorkingHoursRepository;this.records=r.getAll()}getActual=()=>{const r=[];for(const t of this.records)r.push({yearMonth:`${t.year}-${t.month}`,value:t.beingLate.count});return r};getEMA=()=>{const r=Utility.calculateEMA(this.records.map((r=>r.beingLate.count)),12),t=[];for(let e=0;e<this.records.length;e++)t.push({yearMonth:`${this.records[e].year}-${this.records[e].month}`,value:r[e]});return t};getAverage=()=>{const r=Utility.calculateAverage(this.records.map((r=>r.beingLate.count))),t=[];for(const e of this.records)t.push({yearMonth:`${e.year}-${e.month}`,value:r});return t}}export{BeingLateNumberGraphModel};