import{WorkingHoursRepository}from"../../repository/working-hours-repository.js";import{Utility}from"../../shared/utility.js";class OvertimeHoursGraphModel{constructor(){const r=new WorkingHoursRepository;this.records=r.getAll()}getActual=()=>{const r=[];for(const o of this.records)r.push({yearMonth:`${o.year}-${o.month}`,value:o.overtimeHours});return r};getEMA=()=>{const r=Utility.calculateEMA(this.records.map((r=>r.overtimeHours)),12),o=[];for(let t=0;t<this.records.length;t++)o.push({yearMonth:`${this.records[t].year}-${this.records[t].month}`,value:r[t]});return o};getAverage=()=>{const r=Utility.calculateAverage(this.records.map((r=>r.overtimeHours))),o=[];for(const t of this.records)o.push({yearMonth:`${t.year}-${t.month}`,value:r});return o}}export{OvertimeHoursGraphModel};