class AbsoluteURIConverter{constructor(){let t=location.origin;if(t.endsWith("github.io")){let o=location.pathname.indexOf("/",0);if(o=location.pathname.indexOf("/",o+1),-1!==o){t=`${t}${location.pathname.substring(0,o+1)}`}}this.root=t}convert=t=>new URL(t,this.root).href;isAbsoluteURI=t=>!t.indexOf(":")}export{AbsoluteURIConverter};