class Utility{static calculateEMA=(t,e)=>{const a=2/(e+1),l=[t[0]];for(let e=1;e<t.length;e++){const c=t[e]*a+l[e-1]*(1-a);l.push(c)}return l};static calculateAverage=t=>t.reduce(((t,e)=>Number(t)+Number(e)))/t.length;static getNumberOfDays=t=>{const e=t.split("-"),a=new Date(e[0],e[1],e[2]);return 365*a.getFullYear()+30*(a.getMonth()+1)+a.getDate()}}export{Utility};