class Utility{static sleep=t=>new Promise((e=>setTimeout(e,t)));static calculateEMA=(t,e)=>{const s=2/(e+1),a=[t[0]];for(let e=1;e<t.length;e++){const l=t[e]*s+a[e-1]*(1-s);a.push(l)}return a};static calculateAverage=t=>t.reduce(((t,e)=>Number(t)+Number(e)))/t.length;static getNumberOfDays=t=>{const e=t.split("-"),s=new Date(e[0],e[1],e[2]);return 365*s.getFullYear()+30*(s.getMonth()+1)+s.getDate()}}export{Utility};